using Newtonsoft.Json;
using System;

namespace Switcheo.Net.Objects
{
    /// <summary>
    /// Information received after a successful creation of a deposit
    /// </summary>
    public class SwitcheoDepositCreationResult
    {
        /// <summary>
        /// The deposit id generated by Switcheo
        /// </summary>
        [JsonProperty("id")]
        public Guid Id { get; set; }

        /// <summary>
        /// The deposit associated transaction 
        /// </summary>
        [JsonProperty("transaction")]
        public SwitcheoTransaction Transaction { get; set; }

        /// <summary>
        /// The script parameters
        /// </summary>
        [JsonProperty("script_params")]
        public SwitcheoScriptParams ScriptParams { get; set; }
    }
}
